Add-Type -AssemblyName PresentationFramework, PresentationCore, WindowsBase, System.Xaml

$base64Image = @"

"@

function Load-ImageFromBase64([string]$base64) {
    if ([string]::IsNullOrWhiteSpace($base64)) { return $null }
    try {
        $bytes = [Convert]::FromBase64String($base64)
        $stream = New-Object System.IO.MemoryStream(,$bytes)
        $bitmap = New-Object System.Windows.Media.Imaging.BitmapImage
        $bitmap.BeginInit()
        $bitmap.StreamSource = $stream
        $bitmap.CacheOption = [System.Windows.Media.Imaging.BitmapCacheOption]::OnLoad
        $bitmap.EndInit()
        $bitmap.Freeze()
        return $bitmap
    } catch {
        Write-Warning "Error loading Base64 image: $_"
        return $null
    }
}

function Show-CustomMessage {
    param (
        [string]$Text,
        [string]$Title = "Info"
    )
    $XAML = @"
<Window xmlns="http://schemas.microsoft.com/winfx/2006/xaml/presentation"
        xmlns:x="http://schemas.microsoft.com/winfx/2006/xaml"
        Title="$Title" Height="200" Width="300"
        WindowStartupLocation="CenterScreen" Background="Black" ResizeMode="NoResize"
        WindowStyle="None" AllowsTransparency="True" ShowInTaskbar="False"
        Opacity="0.99"
        RenderOptions.EdgeMode="Aliased">
    <Window.Resources>
        <Storyboard x:Key="RedPulseAnimation" RepeatBehavior="Forever" AutoReverse="True">
            <DoubleAnimation Storyboard.TargetName="ButtonGlow" Storyboard.TargetProperty="BlurRadius" From="10" To="40" Duration="0:0:0.8"/>
            <DoubleAnimation Storyboard.TargetName="ButtonGlow" Storyboard.TargetProperty="Opacity" From="0.4" To="1.0" Duration="0:0:0.8"/>
            <ColorAnimation Storyboard.TargetName="ButtonGlow" Storyboard.TargetProperty="Color" To="Red" Duration="0:0:0.01"/>
        </Storyboard>
        <Storyboard x:Key="RedHoverEnterAnimation">
            <DoubleAnimation Storyboard.TargetName="ButtonGlow" Storyboard.TargetProperty="BlurRadius" To="60" Duration="0:0:0.2"/>
            <DoubleAnimation Storyboard.TargetName="ButtonGlow" Storyboard.TargetProperty="Opacity" To="1.0" Duration="0:0:0.2"/>
        </Storyboard>
        <Storyboard x:Key="RedHoverExitAnimation">
            <DoubleAnimation Storyboard.TargetName="ButtonGlow" Storyboard.TargetProperty="BlurRadius" To="40" Duration="0:0:0.2"/>
            <DoubleAnimation Storyboard.TargetName="ButtonGlow" Storyboard.TargetProperty="Opacity" To="1.0" Duration="0:0:0.2"/>
        </Storyboard>

        <Style TargetType="Button">
            <Setter Property="Background" Value="DarkRed"/><Setter Property="Foreground" Value="White"/>
            <Setter Property="FontWeight" Value="Bold"/><Setter Property="BorderThickness" Value="0"/>
            <Setter Property="Cursor" Value="Hand"/><Setter Property="Width" Value="70"/>
            <Setter Property="Height" Value="30"/>
            <Setter Property="Template">
                <Setter.Value>
                    <ControlTemplate TargetType="Button">
                        <Border x:Name="ButtonBorder" Background="{TemplateBinding Background}"
                                BorderBrush="{TemplateBinding BorderBrush}" BorderThickness="{TemplateBinding BorderThickness}"
                                CornerRadius="4">
                            <Border.Effect>
                                <DropShadowEffect x:Name="ButtonGlow" Color="Red" BlurRadius="10" ShadowDepth="0" Opacity="0.4"/>
                            </Border.Effect>
                            <ContentPresenter HorizontalAlignment="Center" VerticalAlignment="Center"/>
                        </Border>
                        <ControlTemplate.Triggers>
                            <EventTrigger RoutedEvent="Loaded">
                                <BeginStoryboard Storyboard="{StaticResource RedPulseAnimation}"/>
                            </EventTrigger>
                            <Trigger Property="IsMouseOver" Value="True">
                                <Setter TargetName="ButtonBorder" Property="Background" Value="#7f6720"/>
                                <Trigger.EnterActions>
                                    <BeginStoryboard Storyboard="{StaticResource RedHoverEnterAnimation}"/>
                                </Trigger.EnterActions>
                                <Trigger.ExitActions>
                                    <BeginStoryboard Storyboard="{StaticResource RedHoverExitAnimation}"/>
                                </Trigger.ExitActions>
                            </Trigger>
                            <Trigger Property="IsPressed" Value="True">
                                <Setter TargetName="ButtonBorder" Property="Background" Value="#8B0000"/>
                            </Trigger>
                        </ControlTemplate.Triggers>
                    </ControlTemplate>
                </Setter.Value>
            </Setter>
        </Style>
    </Window.Resources>
    <Window.Triggers>
        <EventTrigger RoutedEvent="Window.Loaded">
            <BeginStoryboard>
                <Storyboard>
                    <DoubleAnimation Storyboard.TargetProperty="Opacity" From="0.0" To="0.99" Duration="0:0:0.5"/>
                </Storyboard>
            </BeginStoryboard>
        </EventTrigger>
    </Window.Triggers>
    <Border Background="Black" CornerRadius="5" BorderBrush="Gray" BorderThickness="1">
        <Grid Margin="10">
            <Grid.RowDefinitions>
                <RowDefinition Height="*"/>
                <RowDefinition Height="Auto"/>
            </Grid.RowDefinitions>

            <TextBlock Grid.Row="0" Text="$Text" Foreground="White" FontSize="14" TextWrapping="Wrap"
                       VerticalAlignment="Center" HorizontalAlignment="Center" TextAlignment="Center" Margin="0,0,0,10"/>
            
            <Button Grid.Row="1" Content="OK" HorizontalAlignment="Center" VerticalAlignment="Bottom" Name="btnOK"/>
        </Grid>
    </Border>
</Window>
"@
    $reader = New-Object System.IO.StringReader $XAML
    $xmlReader = [System.Xml.XmlReader]::Create($reader)
    $msgWindow = [System.Windows.Markup.XamlReader]::Load($xmlReader)
    $msgWindow.Add_MouseLeftButtonDown({ $msgWindow.DragMove() })
    $btnOK = $msgWindow.FindName("btnOK")
    
    # Fade-Out animation before closing
    $btnOK.Add_Click({
        $fadeout = New-Object System.Windows.Media.Animation.DoubleAnimation
        $fadeout.From = $msgWindow.Opacity # Use current opacity
        $fadeout.To = 0.0
        $fadeout.Duration = New-Object System.Windows.Duration ([TimeSpan]::FromSeconds(0.5))
        
        $storyboard = New-Object System.Windows.Media.Animation.Storyboard
        [System.Windows.Media.Animation.Storyboard]::SetTargetProperty($fadeout, (New-Object System.Windows.PropertyPath("Opacity")))
        $storyboard.Children.Add($fadeout)
        
        # Close the window after the animation completes
        $storyboard.Add_Completed({ $msgWindow.Close() })
        
        $storyboard.Begin($msgWindow)
    })
    $msgWindow.ShowDialog() | Out-Null
}

