    $reader = New-Object System.IO.StringReader $XAML
    $xmlReader = [System.Xml.XmlReader]::Create($reader) # Corrected syntax
    $optionsWindow = [System.Windows.Markup.XamlReader]::Load($xmlReader)

    $optionsWindow.Add_MouseLeftButtonDown({ $optionsWindow.DragMove() })

    # $txtSourcePath is removed
    $txtBackupPath = $optionsWindow.FindName("txtBackupPath")
    $btnSave = $optionsWindow.FindName("btnSave")
    $btnCancel = $optionsWindow.FindName("btnCancel")

    # Return object for the paths
    $result = [PSCustomObject]@{
        BackupPath = $null
        Cancelled = $true
    }

    # Fade-Out animation for Save button
    $btnSave.Add_Click({
        $fadeout = New-Object System.Windows.Media.Animation.DoubleAnimation
        $fadeout.From = $optionsWindow.Opacity # Use current opacity
        $fadeout.To = 0.0
        $fadeout.Duration = New-Object System.Windows.Duration ([TimeSpan]::FromSeconds(0.5))
        
        $storyboard = New-Object System.Windows.Media.Animation.Storyboard
        [System.Windows.Media.Animation.Storyboard]::SetTargetProperty($fadeout, (New-Object System.Windows.PropertyPath("Opacity")))
        $storyboard.Children.Add($fadeout)
        
        $storyboard.Add_Completed({ 
            $result.BackupPath = $txtBackupPath.Text
            $result.Cancelled = $false
            $optionsWindow.Close() 
        })
        
        $storyboard.Begin($optionsWindow)
    })

    $btnCancel.Add_Click({
        $fadeout = New-Object System.Windows.Media.Animation.DoubleAnimation
        $fadeout.From = $optionsWindow.Opacity # Use current opacity
        $fadeout.To = 0.0
        $fadeout.Duration = New-Object System.Windows.Duration ([TimeSpan]::FromSeconds(0.5))
        
        $storyboard = New-Object System.Windows.Media.Animation.Storyboard
        [System.Windows.Media.Animation.Storyboard]::SetTargetProperty($fadeout, (New-Object System.Windows.PropertyPath("Opacity")))
        $storyboard.Children.Add($fadeout)
        
        $storyboard.Add_Completed({ 
            $result.Cancelled = $true
            $optionsWindow.Close() 
        })
        
        $storyboard.Begin($optionsWindow)
    })

    $optionsWindow.ShowDialog() | Out-Null
    return $result
}

function Save-Config([string]$Source, [string]$Backup) {
    $configPath = Join-Path $PSScriptRoot "backup_config.json"
    $config = @{
        SourcePath = $Source
        BackupPath = $Backup
    }
    $config | ConvertTo-Json | Set-Content -Path $configPath -Force
}

function Load-Config {
    $configPath = Join-Path $PSScriptRoot "backup_config.json"
    if (Test-Path $configPath) {
        try {
            return (Get-Content -Path $configPath | ConvertFrom-Json)
        } catch {
            Write-Warning "Error loading configuration: $_. Using default paths."
            return $null
        }
    }
    return $null
}

function Copy-FilesAndOverwrite {
    param ([string]$source, [string]$destination)
    if (-not (Test-Path $source)) {
        throw "Source folder does not exist:`n$source"
    }
    if (-not (Test-Path $destination)) {
        New-Item -ItemType Directory -Path $destination | Out-Null
    }
    Copy-Item -Path "$source\*" -Destination $destination -Recurse -Force
}


