            <BeginStoryboard>
                <Storyboard>
                    <DoubleAnimation Storyboard.TargetProperty="Opacity" From="0.0" To="0.99" Duration="0:0:0.5"/>
                </Storyboard>
            </BeginStoryboard>
        </EventTrigger>
    </Window.Triggers>
    <Border Background='Black' CornerRadius='5' BorderBrush="Gray" BorderThickness="1">
        <Grid Margin='10'>
            <Grid.RowDefinitions>
                <RowDefinition Height="Auto"/> <RowDefinition Height="Auto"/> <RowDefinition Height="Auto"/> <RowDefinition Height="Auto"/> <RowDefinition Height="*"/>    <RowDefinition Height="Auto"/> <RowDefinition Height="Auto"/> <RowDefinition Height="Auto"/> </Grid.RowDefinitions>
            <Grid.ColumnDefinitions>
                <ColumnDefinition Width="*"/>
                <ColumnDefinition Width="*"/>
            </Grid.ColumnDefinitions>

            <Image Name='HeaderImage' Grid.Row="0" Grid.ColumnSpan="2" Height='80' Stretch='Uniform' Margin='0,0,0,15'/>
            <TextBlock Text='LSPD Backup Manager' Grid.Row="1" Grid.ColumnSpan="2" Foreground='White' FontSize='20'
                       FontWeight='Bold' HorizontalAlignment='Center' Margin='0,0,0,15'/>
            <Button Name='SaveBackup' Content='Backup now!' Grid.Row="2" Grid.ColumnSpan="2" Style="{StaticResource RedGlowButtonStyle}"/>
            <Button Name='RecoverBackup' Content='Restore now!' Grid.Row="3" Grid.ColumnSpan="2" Margin='0,5,0,15' Style="{StaticResource RedGlowButtonStyle}"/>
            
            <Button Name='Options' Content='Options' Grid.Row="5" Grid.ColumnSpan="2" Style="{StaticResource BlueGlowButtonStyle}" Margin="0,5,0,5"/>
            <Button Name='Exit' Content='Exit' Grid.Row="6" Grid.ColumnSpan="2" Style="{StaticResource BlueGlowButtonStyle}"/>

            <TextBlock Text="u/huyg" Grid.Row="7" Grid.Column="0" 
                       HorizontalAlignment="Left" VerticalAlignment="Bottom" 
                       Foreground="#7f6720" FontSize="9" FontWeight="Light" Margin="5,5,0,5"/>
            <TextBlock Text="v 1.1" Grid.Row="7" Grid.Column="1" 
                       HorizontalAlignment="Right" VerticalAlignment="Bottom" 
                       Foreground="#7f6720" FontSize="9" FontWeight="Light" Margin="0,5,5,5"/>
        </Grid>
    </Border>
</Window>
"@

# Load window
$reader = New-Object System.IO.StringReader $XAML
$xmlReader = [System.Xml.XmlReader]::Create($reader)
$Window = [System.Windows.Markup.XamlReader]::Load($xmlReader)

$Window.Add_MouseLeftButtonDown({ $Window.DragMove() })

if (-not [string]::IsNullOrWhiteSpace($base64Image.Trim())) {
    try {
        $bitmapImage = Load-ImageFromBase64 $base64Image
        if ($bitmapImage) {
            $Window.FindName("HeaderImage").Source = $bitmapImage
        }
    } catch {
        Show-CustomMessage -Text "Error loading the image." -Title "Image Error"
    }
}

# Button references
$btnSave = $Window.FindName("SaveBackup")
$btnRecover = $Window.FindName("RecoverBackup")
$btnOptions = $Window.FindName("Options")
$btnExit = $Window.FindName("Exit")

# Set the default source path (now fixed)
$global:settings = [PSCustomObject]@{
    SourcePath = Join-Path $env:LOCALAPPDATA "ReadyOrNot\Saved\SaveGames"
    BackupPath = Join-Path ([System.Environment]::GetFolderPath("Desktop")) "RoN Save Backup"
}

# Load configuration
$config = Load-Config
if ($config) {
    # If config exists, use saved backup path (source path is fixed)
    $global:settings.BackupPath = $config.BackupPath
    # Also ensure the source path from config is used if it exists, otherwise use the fixed default.
    # This maintains backward compatibility if an old config with a custom source path exists.
    if (-not [string]::IsNullOrWhiteSpace($config.SourcePath)) {
        $global:settings.SourcePath = $config.SourcePath
    }
} else {
    # If no configuration exists, use the default backup path
    Show-CustomMessage -Text "No existing configuration found. Using default paths.`n`nSource: $($global:settings.SourcePath)`nBackup: $($global:settings.BackupPath)" -Title "Default Paths Set"
}

# Button Events
$btnSave.Add_Click({
    try {
        $ErrorActionPreference = 'Stop'
        Copy-FilesAndOverwrite -source $global:settings.SourcePath -destination $global:settings.BackupPath
        Show-CustomMessage -Text "Backup successfully copied to '$($global:settings.BackupPath)'." -Title "Success"
    } catch {
        Show-CustomMessage -Text "Error during backup:`n$_" -Title "Error"
    }
})

$btnRecover.Add_Click({
    try {
        $ErrorActionPreference = 'Stop'
        Copy-FilesAndOverwrite -source $global:settings.BackupPath -destination $global:settings.SourcePath
        Show-CustomMessage -Text "Backup successfully restored." -Title "Success"
    } catch {
        Show-CustomMessage -Text "Error during restore:`n$_" -Title "Error"
    }
})

$btnOptions.Add_Click({
    # Call options popup and pass current backup path only
    $optionsResult = Show-OptionsPopup -CurrentBackupPath $global:settings.BackupPath

    if (-not $optionsResult.Cancelled) {
        # Update the main script's backup path variable immediately
        $global:settings.BackupPath = $optionsResult.BackupPath
        
        # Save config: sourcePath is now passed correctly again
        Save-Config -Source $global:settings.SourcePath -Backup $global:settings.BackupPath
        Show-CustomMessage -Text "Backup Path has been saved and updated:`n$($global:settings.BackupPath)" -Title "Path Saved"
    }
})

$btnExit.Add_Click({
    # Fade-Out animation for Exit button
    $fadeout = New-Object System.Windows.Media.Animation.DoubleAnimation
    $fadeout.From = $Window.Opacity # Use current opacity
    $fadeout.To = 0.0
    $fadeout.Duration = New-Object System.Windows.Duration ([TimeSpan]::FromSeconds(0.5))
    
    $storyboard = New-Object System.Windows.Media.Animation.Storyboard
    [System.Windows.Media.Animation.Storyboard]::SetTargetProperty($fadeout, (New-Object System.Windows.PropertyPath("Opacity")))
    $storyboard.Children.Add($fadeout)
    
    $storyboard.Add_Completed({ $Window.Close() })
    
    $storyboard.Begin($Window)
})

$Window.ShowDialog() | Out-Null
